  box-shadow: 0 0 1px 1px rgba(0, 0, 0, 0.25), inset 0 1px rgba(255, 255, 255, 0.1);
}
main .progress-bar .percentage {
  padding: 0.25em 0 0 0.25em;
}
main .progress.zero > .progress-bar {
  width: 5%;
  background-color: #f63a0f;
}
main .progress.twenty-five > .progress-bar {
  width: 25%;
  background-color: #f27011;
}
main .progress.fifty > .progress-bar {
  width: 50%;
  background-color: #f2b01e;
}
main .progress.seventy-five > .progress-bar {
  width: 75%;
  background-color: #f2d31b;
}
main .progress.hundred > .progress-bar {
  width: 100%;
  background-color: #86e01e;
}
main .tabular-row {
  display: flex;
  border-bottom: 1px solid #004544;
}
main .tabular-row div {
  padding: 1em;
  flex: 1;
}
main .map-panel {
  display: block;
  position: relative;
  flex: 1;
  padding: 1em;
}
main .map-panel #geocoder {
  position: absolute;
  top: 2em;
  right: 2em;
}
main .map-panel #map {
  width: 100%;
  height: 100%;
  box-shadow: -2px 2px 10px 1px rgba(0, 69, 68, 0.5);
}
main .map-panel #legend.active {
  display: block;
  position: absolute;
  top: 4em;
  left: 2em;
  width: 25em;
  z-index: 2;
}
main .map-panel #legend.active strong {
  background-color: rgba(255, 255, 255, 0.7);
}
main .map-panel #legend.active .legend label,
main .map-panel #legend.active .legend span {
  background-color: rgba(255, 255, 255, 0.7);
  display: block;
  float: left;
  height: 15px;
  width: 25%;
  text-align: center;
  font-size: 0.75em;
}
main .map-panel #legend {
  display: none;
}
